let name=document.querySelector("#name"),email=document.querySelector("#email"),phone=document.querySelector("#phone"),sendButton=document.querySelector("#send-button"),form=document.querySelector(".contacts-form-main"),check=0;function submit(){form.reset(),alert("Your application has been successfully accepted!")}function testString(){let e=name.value;/^[a-zA-Z]+$/.test(e)||/^[а-яА-Я]+$/.test(e)?(name.style.backgroundColor="transparent",check=0):(name.style.backgroundColor="rgb(245, 170, 170)",check=1)}function testEmail(){let e=email.value;/^([a-z0-9_-]+\.)*[a-z0-9_-]+@[a-z0-9_-]+(\.[a-z0-9_-]+)*\.[a-z]{2,6}$/.test(e)?(email.style.backgroundColor="transparent",check=0):(email.style.backgroundColor="rgb(245, 170, 170)",check=1)}function testPhone(){let e=phone.value;/^\+\d{2}\(\d{3}\)\d{3}-\d{2}-\d{2}$/.test(e)?(phone.style.backgroundColor="transparent",check=0):(phone.style.backgroundColor="rgb(245, 170, 170)",check=1)}name.addEventListener("blur",(function(e){testString()})),email.addEventListener("blur",(function(e){testEmail()})),phone.addEventListener("blur",(function(e){testPhone()})),form.addEventListener("submit",(function(e){e.preventDefault(),0==check&&""!=name.value&&""!=email.value&&""!=phone.value?submit():1==check?alert("You entered incorrect values!"):""!=name.value||""!=email.value||""!=phone.value?alert("Fill in the blank fields!"):alert("You need to fill in the fields!")}));