let video=document.querySelector(".block-video"),btnPlayVideo=document.querySelector(".btn-play"),svg=document.querySelector(".svg");function removeControls(o){o.removeAttribute("controls")}function showControls(o){o.setAttribute("controls","")}window.onload=removeControls(video),btnPlayVideo.onclick=function(){video.play(),svg.classList.add("hidden-playing"),btnPlayVideo.classList.add("hidden-playing"),showControls(video)};